const sizes = ["Small", "Medium", "Large"];

return (
  <RadioGroup
    defaultValue="Medium"
    orientation="horizontal"
    class="flex-row gap-4"
  >
    <RadioGroupLabel>Size</RadioGroupLabel>
    <For each={sizes}>
      {(size) => <RadioGroupItem value={size}>{size}</RadioGroupItem>}
    </For>
  </RadioGroup>
);

const frameworks = [
  { value: "react", label: "React", icon: "atom" },
  { value: "solid", label: "Solid", icon: "box" },
  { value: "vue", label: "Vue", icon: "triangle" },
  { value: "svelte", label: "Svelte", icon: "zap" },
];

return (
  <RadioGroup defaultValue="solid">
    <RadioGroupLabel>Framework</RadioGroupLabel>
    <For each={frameworks}>
      {(framework) => (
        <RadioGroupItem value={framework.value}>
          <div class="flex items-center gap-2">
            <div class={cn("h-4 w-4", icon(framework.icon))} />
            <span>{framework.label}</span>
          </div>
        </RadioGroupItem>
      )}
    </For>
  </RadioGroup>
);

const options = [
  {
    value: "starter",
    label: "Starter",
    desc: "Perfect for beginners",
    disabled: false,
  },
  {
    value: "pro",
    label: "Pro",
    desc: "For professional developers",
    disabled: false,
  },
  {
    value: "enterprise",
    label: "Enterprise",
    desc: "Advanced features for teams",
    disabled: true,
  },
];

return (
  <RadioGroup defaultValue="pro">
    <RadioGroupLabel>Select a plan</RadioGroupLabel>
    <For each={options}>
      {(option) => (
        <RadioGroupItem value={option.value} disabled={option.disabled}>
          <div class="flex flex-col gap-0.5">
            <span class="text-sm font-medium">{option.label}</span>
            <span class="text-xs text-muted-foreground">
              {option.desc}
            </span>
          </div>
        </RadioGroupItem>
      )}
    </For>
  </RadioGroup>
);

