const initialData = Array.from({ length: 20 }, () => Math.floor(Math.random() * 100) + 20);
const [data, setData] = createSignal(initialData);
let chartRef: echarts.ECharts | undefined;

onMount(() => {
	const interval = setInterval(() => {
		setData((prev) => {
